import React, { useState } from 'react'
import './style.css'
import axios from 'axios';


function Login() {
   const [Username, setUsername] = useState("");
   const [pswd,SetPswd]=useState("");

var checkuname=(e)=>{
    setUsername(e.target.value)
   console.log(Username)
}
var checkpswd=(e)=>{
    SetPswd(e.target.value)
    console.log(pswd)
}

const passdata = async () => {
    try {

        console.log(Username)
        console.log(pswd)
      const response = await axios.get("http://localhost:7000", { 
        headers : {
            'Content-Type':'application/json',
        },
        body:JSON.stringify({
            Username,pswd,
        }) 

        
      });
    //   setresult(response.data.result);
      console.log(response.data.result);
    } catch (error) {
      console.error(error);
    }
  };
  

  return (
    <div>
        <h1 className='heading'>Login Page</h1>

        <input type='text'  className='uname' value={Username}  onChange={(e)=>checkuname(e)} placeholder='Username'/><br/>
        <input type='password'  className='pswd'value={pswd} onChange={(e)=>checkpswd(e)} placeholder='Password'/>
        <button onClick={()=>passdata()}>Login</button>


    </div>
  )
}

export default Login
